import React, { useState, useEffect, useMemo } from "react";
import { Link } from "react-router-dom";
import supabase from "../utils/supabaseClient";
import { useAuth } from "../context/AuthContext";
import { Bookmark, Briefcase, MapPin, Search, Users, Clock, Globe, PlusCircle } from "lucide-react";

const Task = () => {
  const { user } = useAuth();
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("All");
  const [jobType, setJobType] = useState("All");
  const [location, setLocation] = useState("");
  const [sortOrder, setSortOrder] = useState("None");
  const [tasks, setTasks] = useState([]);
  const [bids, setBids] = useState({});
  const [savedJobs, setSavedJobs] = useState(new Set());
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [newTask, setNewTask] = useState({
    name: "",
    category: "Design",
    job_type: "Full-time",
    location: "",
    budget: "",
  });

  const [posting, setPosting] = useState(false);
  const [postError, setPostError] = useState(null);

  useEffect(() => {
    if (user) {
      const fetchTasks = async () => {
        setLoading(true);
        try {
          const { data, error } = await supabase.from("tasks").select("*");
          if (error) throw error;
          setTasks(data || []);
        } catch (err) {
          setError("Failed to load jobs. Please try again.");
        } finally {
          setLoading(false);
        }
      };
      fetchTasks();
    }
  }, [user]);

  useEffect(() => {
    const fetchBids = async () => {
      try {
        const { data, error } = await supabase.from("bids").select("*");
        if (error) throw error;
        setBids(
          data.reduce((acc, bid) => {
            acc[bid.task_id] = [...(acc[bid.task_id] || []), bid];
            return acc;
          }, {})
        );
      } catch (err) {
        console.error("Error fetching bids:", err.message);
      }
    };
    fetchBids();
  }, []);

  const filteredTasks = useMemo(() => {
    return tasks
      .filter((task) =>
        task.name.toLowerCase().includes(search.toLowerCase()) &&
        (category === "All" || task.category === category) &&
        (jobType === "All" || task.job_type === jobType) &&
        (location === "" || task.location.toLowerCase().includes(location.toLowerCase()))
      )
      .sort((a, b) => {
        if (sortOrder === "Low to High") return (a.budget || 0) - (b.budget || 0);
        if (sortOrder === "High to Low") return (b.budget || 0) - (a.budget || 0);
        return 0;
      });
  }, [search, category, jobType, location, sortOrder, tasks]);

  const handleSaveJob = (taskId) => {
    setSavedJobs((prev) => new Set(prev.has(taskId) ? [...prev].filter((id) => id !== taskId) : [...prev, taskId]));
  };

  const handlePostTask = async (e) => {
    e.preventDefault();
    setPosting(true);
    setPostError(null);

    if (!user) {
      setPostError("You must be logged in to post a task.");
      setPosting(false);
      return;
    }

    try {
      const { data, error } = await supabase.from("tasks").insert([
        {
          name: newTask.name,
          category: newTask.category,
          job_type: newTask.job_type,
          location: newTask.location,
          budget: newTask.budget ? parseFloat(newTask.budget) : null,
          user_id: user.id,
        },
      ]);

      if (error) throw error;

      setTasks([...tasks, ...data]);
      setNewTask({ name: "", category: "Design", job_type: "Full-time", location: "", budget: "" });
    } catch (err) {
      setPostError("Failed to post task. Try again.");
    } finally {
      setPosting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex justify-center">
      <div className="w-full max-w-5xl bg-white shadow-lg p-6 rounded-lg">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">Find Your Next Opportunity</h2>

        {/* Post a Task Form */}
        <div className="bg-blue-50 p-4 rounded-lg mb-6">
          <h3 className="text-xl font-semibold mb-4 text-blue-700 flex items-center">
            <PlusCircle className="h-6 w-6 mr-2" /> Post a Task
          </h3>
          {postError && <p className="text-red-500">{postError}</p>}
          <form onSubmit={handlePostTask} className="flex flex-wrap gap-4">
            <input
              type="text"
              placeholder="Task name"
              value={newTask.name}
              onChange={(e) => setNewTask({ ...newTask, name: e.target.value })}
              className="p-2 border rounded w-full md:w-auto flex-grow"
              required
            />
            <select
              value={newTask.category}
              onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
              className="p-2 border rounded"
            >
              <option value="Design">Design</option>
              <option value="Programming">Programming</option>
              <option value="Marketing">Marketing</option>
            </select>
            <select
              value={newTask.job_type}
              onChange={(e) => setNewTask({ ...newTask, job_type: e.target.value })}
              className="p-2 border rounded"
            >
              <option value="Full-time">Full-time</option>
              <option value="Part-time">Part-time</option>
              <option value="Contract">Contract</option>
              <option value="Remote">Remote</option>
            </select>
            <input
              type="text"
              placeholder="Location"
              value={newTask.location}
              onChange={(e) => setNewTask({ ...newTask, location: e.target.value })}
              className="p-2 border rounded"
            />
            <input
              type="number"
              placeholder="Budget ($)"
              value={newTask.budget}
              onChange={(e) => setNewTask({ ...newTask, budget: e.target.value })}
              className="p-2 border rounded w-28"
            />
            <button type="submit" disabled={posting} className="p-2 bg-blue-600 text-white rounded hover:bg-blue-700">
              {posting ? "Posting..." : "Post Task"}
            </button>
          </form>
        </div>

        {/* Job Listings */}
        {loading ? (
          <p className="text-center text-gray-600">Loading jobs...</p>
        ) : error ? (
          <p className="text-center text-red-500">{error}</p>
        ) : filteredTasks.length > 0 ? (
          <ul className="space-y-6">
            {filteredTasks.map((task) => (
              <li key={task.id} className="p-4 border rounded shadow-md bg-white hover:shadow-lg transition">
                <div className="flex justify-between items-center">
                  <h3 className="text-xl font-semibold text-gray-800">{task.name}</h3>
                  <button onClick={() => handleSaveJob(task.id)} className="text-gray-500 hover:text-blue-500">
                    <Bookmark className={`h-6 w-6 ${savedJobs.has(task.id) ? "text-blue-500" : ""}`} />
                  </button>
                </div>
                <p className="text-gray-600 flex items-center">
                  <Briefcase className="h-5 w-5 mr-2 text-blue-500" /> {task.job_type}
                </p>
                <Link to={`/tasks/${task.id}`} className="text-blue-500 hover:underline block mt-2">View Details</Link>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-center text-gray-500 mt-6">No jobs found.</p>
        )}
      </div>
    </div>
  );
};

export default Task;
