import { useState } from "react";
import { loginUser } from "../services/authService";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "", rememberMe: false });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      await loginUser(formData);
      navigate("/dashboard"); 
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md">
        <h2 className="text-2xl font-bold text-center text-gray-800">Sign in</h2>
        <p className="text-gray-600 text-center mb-6">Stay updated on your professional world</p>

        {error && <p className="text-red-500 text-center mb-4">{error}</p>}

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            className="border p-3 w-full rounded-lg"
            required
          />

          <div className="relative">
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              className="border p-3 w-full rounded-lg"
              required
            />
            <span
              onClick={() => setShowPassword(!showPassword)}
              className="absolute right-3 top-3 cursor-pointer text-gray-600"
            >
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </span>
          </div>

          <div className="flex justify-between items-center">
            <label className="flex items-center text-gray-600">
              <input
                type="checkbox"
                name="rememberMe"
                checked={formData.rememberMe}
                onChange={handleChange}
                className="mr-2"
              />
              Remember me
            </label>
            <a href="/forgot-password" className="text-blue-600 text-sm">
              Forgot password?
            </a>
          </div>

          <button
            type="submit"
            className={`w-full p-3 text-white font-bold rounded-lg ${
              loading ? "bg-blue-400 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
            }`}
            disabled={loading}
          >
            {loading ? "Signing in..." : "Sign in"}
          </button>
        </form>

        <div className="my-4 text-center text-gray-500">or</div>

        <button className="w-full p-3 text-gray-700 bg-white border border-gray-300 rounded-lg flex items-center justify-center hover:bg-gray-200">
          <img src="/google-logo.png" alt="Google" className="w-5 h-5 mr-2" />
          Sign in with Google
        </button>

        <button className="w-full p-3 text-gray-700 bg-white border border-gray-300 rounded-lg flex items-center justify-center mt-2 hover:bg-gray-200">
          <img src="/linkedin-logo.png" alt="LinkedIn" className="w-5 h-5 mr-2" />
          Sign in with LinkedIn
        </button>

        <p className="text-center text-gray-600 mt-6">
          New to our platform?{" "}
          <a href="/signup" className="text-blue-600 font-semibold">
            Join now
          </a>
        </p>
      </div>
    </div>
  );
};

export default Login;
