import React, { useState, useEffect } from "react";
import supabase from "../utils/supabaseClient";

const Messages = ({ userId }) => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [typing, setTyping] = useState(false);
  const [replyingTo, setReplyingTo] = useState(null);

  useEffect(() => {
    const fetchMessages = async () => {
      const { data, error } = await supabase
        .from("messages")
        .select("*")
        .order("created_at", { ascending: false });

      if (error) console.error("Error fetching messages:", error);
      else setMessages(data);
    };

    fetchMessages();

    // Subscribe to real-time messages
    const channel = supabase
      .channel("messages")
      .on(
        "postgres_changes",
        { event: "INSERT", schema: "public", table: "messages" },
        (payload) => {
          setMessages((prevMessages) => [payload.new, ...prevMessages]);
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, []);

  const sendMessage = async () => {
    if (!message.trim()) return;
    const newMessage = {
      user_id: userId,
      content: message,
      reply_to: replyingTo ? replyingTo.id : null,
      created_at: new Date().toISOString(),
    };

    const { error } = await supabase.from("messages").insert([newMessage]);

    if (error) console.error("Error sending message:", error);
    else {
      setMessage("");
      setReplyingTo(null);
    }
  };

  return (
    <div className="p-4 max-w-lg mx-auto">
      <h2 className="text-2xl font-bold text-center mb-4">Chat</h2>

      {/* Typing Indicator */}
      {typing && <p className="text-sm text-gray-500">Someone is typing...</p>}

      {/* Messages */}
      <div className="mb-4 border p-3 rounded-lg h-80 overflow-y-auto bg-gray-100">
        {messages.map((msg) => (
          <div
            key={msg.id}
            className={`p-2 mb-2 rounded-lg ${
              msg.user_id === userId ? "bg-blue-500 text-white self-end" : "bg-white"
            }`}
          >
            {msg.reply_to && (
              <p className="text-xs italic text-gray-700 border-l-4 pl-2 mb-1">
                Replying to: {messages.find((m) => m.id === msg.reply_to)?.content}
              </p>
            )}
            <p>{msg.content}</p>
            <p className="text-xs text-gray-400 text-right">{new Date(msg.created_at).toLocaleTimeString()}</p>
          </div>
        ))}
      </div>

      {/* Replying UI */}
      {replyingTo && (
        <div className="p-2 bg-gray-200 rounded-md mb-2">
          <p className="text-sm italic">Replying to: {replyingTo.content}</p>
          <button onClick={() => setReplyingTo(null)} className="text-sm text-red-600">
            Cancel
          </button>
        </div>
      )}

      {/* Message Input */}
      <div className="flex space-x-2">
        <input
          type="text"
          className="w-full border p-2 rounded-md"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => {
            setMessage(e.target.value);
            setTyping(true);
            setTimeout(() => setTyping(false), 2000);
          }}
        />
        <button onClick={sendMessage} className="bg-blue-600 text-white px-4 py-2 rounded-md">
          Send
        </button>
      </div>
    </div>
  );
};

export default Messages;
