import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext"; // ✅ Import useAuth

const Settings = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [profileVisibility, setProfileVisibility] = useState("public");
  const [emailNotifications, setEmailNotifications] = useState(true);

  const navigate = useNavigate();
  const { user } = useAuth(); // ✅ Get logged-in user

  return (
    <div className={`min-h-screen ${darkMode ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900"} p-6`}>
      <div className="max-w-5xl mx-auto">
        <h1 className="text-4xl font-extrabold text-center">Settings</h1>
        <p className="mt-4 text-lg text-center">Manage your account preferences.</p>

        {/* Edit Profile */}
        <div className="mt-8 bg-white p-6 rounded-2xl shadow-lg">
          <h2 className="text-2xl font-bold">Account Preferences</h2>
          <button 
            onClick={() => navigate(`/profile/${user?.id}`)} // ✅ Navigate to correct profile route
            className="mt-3 px-4 py-2 bg-blue-600 text-white rounded-lg shadow-md hover:bg-blue-700"
          >
            Edit Profile
          </button>
        </div>

        {/* Privacy Settings */}
        <div className="mt-6 bg-white p-6 rounded-2xl shadow-lg">
          <h2 className="text-2xl font-bold">Privacy Settings</h2>
          <label className="block mt-3">
            <span className="text-lg font-bold">Profile Visibility</span>
            <select 
              value={profileVisibility} 
              onChange={(e) => setProfileVisibility(e.target.value)}
              className="mt-2 block w-full p-2 border rounded-lg"
            >
              <option value="public">Public</option>
              <option value="connections">Connections Only</option>
              <option value="private">Private</option>
            </select>
          </label>
        </div>

        {/* Notification Settings */}
        <div className="mt-6 bg-white p-6 rounded-2xl shadow-lg">
          <h2 className="text-2xl font-bold">Notification Settings</h2>
          <label className="flex items-center justify-between mt-3">
            <span className="text-lg font-bold">Email Notifications</span>
            <input 
              type="checkbox" 
              checked={emailNotifications}
              onChange={() => setEmailNotifications(!emailNotifications)}
              className="w-5 h-5"
            />
          </label>
        </div>

        {/* Dark Mode Toggle */}
        <div className="mt-6 bg-white p-6 rounded-2xl shadow-lg">
          <h2 className="text-2xl font-bold">Appearance</h2>
          <label className="flex items-center justify-between mt-3">
            <span className="text-lg font-bold">Dark Mode</span>
            <button 
              onClick={() => setDarkMode(!darkMode)}
              className={`px-4 py-2 rounded-lg transition ${darkMode ? "bg-yellow-400 text-black" : "bg-gray-700 text-white"}`}
            >
              {darkMode ? "Disable" : "Enable"}
            </button>
          </label>
        </div>
      </div>
    </div>
  );
};

export default Settings;
