import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import supabase from "../utils/supabaseClient";
import { Edit, UploadCloud, Briefcase, GraduationCap, Globe, Link as LinkIcon } from "lucide-react";

const UserProfile = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    bio: "",
    skills: "",
    experience: "",
    education: "",
    linkedin: "",
    github: "",
    portfolio: "",
    profile_picture: "",
  });

  useEffect(() => {
    if (!id) {
      console.error("No user ID found in URL");
      return;
    }

    const fetchUser = async () => {
      const { data, error } = await supabase.from("users").select("*").eq("id", id).single();

      if (error) {
        console.error("Error fetching user:", error);
        alert("Failed to fetch user data.");
      } else {
        setUser(data);
        setFormData(data);
      }

      setLoading(false);
    };

    fetchUser();
  }, [id]);

  const handleEdit = () => setEditing(!editing);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    const { error } = await supabase.from("users").update(formData).eq("id", id);
    if (error) {
      console.error("Error updating profile:", error);
      alert("Failed to update profile.");
    } else {
      setUser(formData);
      setEditing(false);
    }
  };

  if (loading) return <p className="text-center text-gray-500">Loading user profile...</p>;
  if (!user) return <p className="text-center text-red-500">User not found.</p>;

  return (
    <div className="max-w-4xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden">
      {/* Cover Photo */}
      <div className="h-40 bg-gray-300">
        <img
          src="https://source.unsplash.com/random/1200x400?nature"
          alt="Cover"
          className="w-full h-full object-cover"
        />
      </div>

      {/* Profile Header */}
      <div className="p-6 relative">
        {/* Profile Picture */}
        <div className="relative w-24 h-24 rounded-full overflow-hidden border-4 border-white shadow-lg absolute -top-12 left-6">
          <img
            src={user.profile_picture || "https://i.pravatar.cc/100"}
            alt="Profile"
            className="w-full h-full object-cover"
          />
        </div>

        {/* Edit Button */}
        <button
          className="absolute top-4 right-4 bg-gray-200 hover:bg-gray-300 p-2 rounded-full"
          onClick={handleEdit}
        >
          <Edit className="h-5 w-5" />
        </button>

        <div className="mt-6 ml-32">
          <h1 className="text-2xl font-bold">{user.name}</h1>
          <p className="text-gray-500">{user.email}</p>
          <p className="mt-2 text-gray-700">{user.bio || "No bio available."}</p>
        </div>
      </div>

      {/* User Details */}
      <div className="p-6">
        {/* Skills */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2">Skills</h2>
          <p className="text-gray-700">{user.skills || "No skills added yet."}</p>
        </div>

        {/* Experience */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2 flex items-center">
            <Briefcase className="h-5 w-5 mr-2 text-blue-500" /> Experience
          </h2>
          <p className="text-gray-700">{user.experience || "No experience details available."}</p>
        </div>

        {/* Education */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2 flex items-center">
            <GraduationCap className="h-5 w-5 mr-2 text-green-500" /> Education
          </h2>
          <p className="text-gray-700">{user.education || "No education details available."}</p>
        </div>

        {/* Social Links */}
        <div>
          <h2 className="text-xl font-semibold mb-2 flex items-center">
            <Globe className="h-5 w-5 mr-2 text-indigo-500" /> Social Links
          </h2>
          <ul className="text-blue-600">
            {user.linkedin && (
              <li>
                <a href={user.linkedin} target="_blank" rel="noopener noreferrer" className="flex items-center">
                  <LinkIcon className="h-4 w-4 mr-2" /> LinkedIn
                </a>
              </li>
            )}
            {user.github && (
              <li>
                <a href={user.github} target="_blank" rel="noopener noreferrer" className="flex items-center">
                  <LinkIcon className="h-4 w-4 mr-2" /> GitHub
                </a>
              </li>
            )}
            {user.portfolio && (
              <li>
                <a href={user.portfolio} target="_blank" rel="noopener noreferrer" className="flex items-center">
                  <LinkIcon className="h-4 w-4 mr-2" /> Portfolio
                </a>
              </li>
            )}
          </ul>
        </div>
      </div>

      {/* Edit Form */}
      {editing && (
        <div className="p-6 border-t">
          <h2 className="text-xl font-semibold mb-4">Edit Profile</h2>
          <input
            type="text"
            name="bio"
            value={formData.bio}
            onChange={handleChange}
            placeholder="Update your bio"
            className="w-full p-2 border rounded mb-4"
          />
          <input
            type="text"
            name="skills"
            value={formData.skills}
            onChange={handleChange}
            placeholder="Update skills (e.g. React, Python)"
            className="w-full p-2 border rounded mb-4"
          />
          <input
            type="text"
            name="experience"
            value={formData.experience}
            onChange={handleChange}
            placeholder="Update experience"
            className="w-full p-2 border rounded mb-4"
          />
          <input
            type="text"
            name="education"
            value={formData.education}
            onChange={handleChange}
            placeholder="Update education"
            className="w-full p-2 border rounded mb-4"
          />
          <button onClick={handleSave} className="bg-blue-500 text-white px-4 py-2 rounded">
            Save Changes
          </button>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
