import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";
import { TaskProvider } from "./context/TaskContext";
import supabase from "./config/supabaseClient";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Home from "./pages/Home";
import Tasks from "./pages/Tasks";
import MyTasks from "./pages/MyTasks";
import MyBids from "./components/MyBids";
import Settings from "./pages/Settings";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./components/Dashboard";
import Header from "./components/Header";
import Footer from "./components/Footer";
import TaskDetail from "./components/TaskDetail";
import CreateTask from "./components/CreateTask";
import Bidding from "./components/Bidding";
import UserProfile from "./pages/UserProfile";
import Messages from "./pages/Messages";
import Groups from "./pages/Groups";
import Posts from "./pages/Posts";

function App() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);
  const [authLoading, setAuthLoading] = useState(true);

  const notificationCooldown = 5000;
  let lastNotificationTime = 0;

  const showBrowserNotification = (title, body) => {
    const now = Date.now();
    if (now - lastNotificationTime < notificationCooldown) return;
    lastNotificationTime = now;

    if ("Notification" in window && Notification.permission === "granted") {
      new Notification(title, { body, icon: "/logo192.png" });
    }
  };

  const requestNotificationPermission = async () => {
    if ("Notification" in window && Notification.permission === "default") {
      await Notification.requestPermission();
    }
  };

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase.from("tasks").select("*");
      if (error) throw error;
      setTasks(data || []);
    } catch (err) {
      console.error("Error fetching tasks:", err.message);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
    requestNotificationPermission();

    const channel = supabase
      .channel("tasks-realtime")
      .on(
        "postgres_changes",
        { event: "INSERT", schema: "public", table: "tasks" },
        (payload) => {
          console.log("New task added:", payload.new);
          setTasks((prev) => [...prev, payload.new]);
          toast.success(` New Task: ${payload.new.title}`);
          showBrowserNotification("New Task Available!", payload.new.title);
        }
      )
      .on(
        "postgres_changes",
        { event: "UPDATE", schema: "public", table: "tasks" },
        (payload) => {
          console.log("Task updated:", payload.new);
          setTasks((prev) =>
            prev.map((task) => (task.id === payload.new.id ? payload.new : task))
          );
          toast.info(`✏️ Task Updated: ${payload.new.title}`);
          showBrowserNotification("Task Updated!", payload.new.title);
        }
      )
      .on(
        "postgres_changes",
        { event: "DELETE", schema: "public", table: "tasks" },
        (payload) => {
          console.log("Task deleted:", payload.old);
          setTasks((prev) => prev.filter((task) => task.id !== payload.old.id));
          toast.error("🗑️ Task Removed");
          showBrowserNotification("Task Removed!", "A task was deleted.");
        }
      )
      .subscribe();

    return () => {
      channel.unsubscribe();
    };
  }, []);

  useEffect(() => {
    const checkSession = async () => {
      const { data: { session } } = await supabase.auth.getSession();
      setUser(session?.user || null);
      setAuthLoading(false);
    };
    checkSession();

    const { data: authListener } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user || null);
    });

    return () => {
      authListener?.subscription?.unsubscribe();
    };
  }, []);

  if (authLoading) return <div>Loading...</div>;

  return (
    <AuthProvider>
      <TaskProvider>
        <Router>
          <Header />
          <ToastContainer position="top-right" autoClose={5000} />
          <div className="container mx-auto p-4">
            <Routes>
              <Route path="/" element={user ? <Home /> : <Navigate to="/register" />} />
              <Route path="/tasks" element={user ? <Tasks tasks={tasks} loading={loading} error={error} /> : <Navigate to="/register" />} />
              <Route path="/tasks/:id" element={user ? <TaskDetail /> : <Navigate to="/register" />} />
              <Route path="/my-tasks" element={user ? <MyTasks /> : <Navigate to="/register" />} />
              <Route path="/my-bids" element={user ? <MyBids /> : <Navigate to="/register" />} />
              <Route path="/dashboard" element={user ? <Dashboard /> : <Navigate to="/register" />} />
              <Route path="/settings" element={user ? <Settings /> : <Navigate to="/register" />} />
              <Route path="/messages" element={user ? <Messages /> : <Navigate to="/register" />} />
              <Route path="/groups" element={user ? <Groups /> : <Navigate to="/register" />} />
              <Route path="/posts" element={user ? <Posts /> : <Navigate to="/register" />} />
              <Route path="/user-profile" element={user ? <UserProfile /> : <Navigate to="/register" />} />
              <Route path="/create-task" element={user ? <CreateTask /> : <Navigate to="/register" />} />
              <Route path="/bidding" element={user ? <Bidding /> : <Navigate to="/register" />} />

              {/* Authentication Routes */}
              <Route path="/login" element={user ? <Navigate to="/" /> : <Login />} />
              <Route path="/register" element={user ? <Navigate to="/" /> : <Register />} />
            </Routes>
          </div>
          <Footer />
        </Router>
      </TaskProvider>
    </AuthProvider>
  );
}

export default App;
