import React, { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext"; // Import useAuth
import { TaskProvider } from "./context/TaskContext";
import supabase from "./utils/supabaseClient";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Home from "./pages/Home";
import Tasks from "./pages/Tasks";
import TaskDetail from "./components/TaskDetail";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Header from "./components/Header";
import Footer from "./components/Footer";

function App() {
  const { user, setUser } = useAuth(); // Use AuthContext
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); 
  const [authLoading, setAuthLoading] = useState(true);

  const notificationCooldown = 5000;
  const lastNotificationTime = useRef(0);

  const showBrowserNotification = (title, body) => {
    const now = Date.now();
    if (now - lastNotificationTime.current < notificationCooldown) return;
    lastNotificationTime.current = now;

    if ("Notification" in window && Notification.permission === "granted") {
      new Notification(title, { body, icon: "/logo192.png" });
    }
  };

  const requestNotificationPermission = async () => {
    if ("Notification" in window && Notification.permission === "default") {
      await Notification.requestPermission();
    }
  };

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase.from("tasks").select("*");
      if (error) throw error;
      setTasks(data || []);
    } catch (err) {
      console.error("Error fetching tasks:", err.message);
      setError(err.message);  
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
    requestNotificationPermission();

    const channel = supabase
      .channel("tasks-realtime")
      .on(
        "postgres_changes",
        { event: "INSERT", schema: "public", table: "tasks" },
        (payload) => {
          console.log("New task added:", payload.new);
          setTasks((prev) => [...prev, payload.new]);
          toast.success(` New Task: ${payload.new.title}`);
          showBrowserNotification("New Task Available!", payload.new.title);
        }
      )
      .on(
        "postgres_changes",
        { event: "UPDATE", schema: "public", table: "tasks" },
        (payload) => {
          console.log("Task updated:", payload.new);
          setTasks((prev) =>
            prev.map((task) => (task.id === payload.new.id ? payload.new : task))
          );
          toast.info(`✏️ Task Updated: ${payload.new.title}`);
          showBrowserNotification("Task Updated!", payload.new.title);
        }
      )
      .on(
        "postgres_changes",
        { event: "DELETE", schema: "public", table: "tasks" },
        (payload) => {
          console.log("Task deleted:", payload.old);
          setTasks((prev) => prev.filter((task) => task.id !== payload.old.id));
          toast.error("Task Removed");
          showBrowserNotification("Task Removed!", "A task was deleted.");
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, []); 

  useEffect(() => {
    const checkSession = async () => {
      const { data: { session } } = await supabase.auth.getSession();

      if (session?.user && session?.user.id !== user?.id) {
        setUser(session.user);
        localStorage.setItem("user", JSON.stringify(session.user));
      }
      setAuthLoading(false); 
    };

    const savedUser = localStorage.getItem("user");
    if (savedUser) {
      setUser(JSON.parse(savedUser));
      setAuthLoading(false);
    } else {
      checkSession();
    }

    const { data: authListener } = supabase.auth.onAuthStateChange((_event, session) => {
      if (session?.user && session?.user.id !== user?.id) {
        setUser(session.user);
        localStorage.setItem("user", JSON.stringify(session.user)); 
      } else {
        localStorage.removeItem("user"); 
        setUser(null);
      }
    });

    return () => {
      authListener?.subscription?.unsubscribe();
    };
  }, [setUser, user]);

  if (authLoading) return <div>Loading...</div>;

  return (
    <AuthProvider>
      <TaskProvider>
        <ToastContainer position="top-right" autoClose={5000} />
        <Router>
          <Header />
          <div className="container mx-auto p-4">
            <Routes>
              <Route
                path="/"
                element={
                  <RequireAuth authLoading={authLoading}>
                    <Home />
                  </RequireAuth>
                }
              />
              <Route
                path="/tasks"
                element={
                  <RequireAuth authLoading={authLoading}>
                    <Tasks tasks={tasks} loading={loading} error={error} />
                  </RequireAuth>
                }
              />
              <Route
                path="/tasks/:id"
                element={
                  <RequireAuth authLoading={authLoading}>
                    <TaskDetail />
                  </RequireAuth>
                }
              />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
            </Routes>
          </div>
          <Footer />
        </Router>
      </TaskProvider>
    </AuthProvider>
  );
}

function RequireAuth({ children, authLoading }) {
  const { user } = useAuth();

  if (authLoading) return <div>Loading...</div>;
  if (!user) return <Navigate to="/login" />;
  return children;
}

export default App;
