import React, { createContext, useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { registerUser, loginUser, logoutUser } from "../services/authService";


export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        console.log("Fetching user...");

        const { data: authUser, error } = await supabase.auth.getUser();
        console.log("User data:", authUser);

        if (error || !authUser?.user) {
          console.error("Error fetching user:", error);
          return;
        }

        const { data: profile, error: profileError } = await supabase
          .from("profiles")
          .select("*")
          .eq("id", authUser.user.id)
          .single();

        if (profileError) {
          console.error("Error fetching profile:", profileError);
        } else {
          console.log("Profile data:", profile);
        }
      } catch (err) {
        console.error("Unexpected error:", err);
      }
    };

    fetchUserProfile();
  }, []);



  const getAuthToken = () => localStorage.getItem("token");


  useEffect(() => {
    checkUserLoggedIn();
  }, []);

  const checkUserLoggedIn = async () => {
    try {
      const token = getAuthToken();
      const storedUser = localStorage.getItem("user");

      if (token && storedUser) {
        setUser(JSON.parse(storedUser));
        console.log("User restored from localStorage:", JSON.parse(storedUser));
      } else {
        setUser(null); 
      }
    } catch (error) {
      console.error("Authentication check failed:", error);
    } finally {
      setLoading(false);
    }
  };


  const login = async (credentials) => {
    try {
      const response = await loginUser(credentials);
      if (response.user && response.access_token) {
        localStorage.setItem("token", response.access_token);
        localStorage.setItem("user", JSON.stringify(response.user));
        setUser(response.user);
        console.log("User logged in:", response.user);
        navigate("/"); 
        return { success: true };
      }
      return { success: false, error: response.error };
    } catch (error) {
      return { success: false, error: "Login failed. Try again." };
    }
  };


  const logout = async () => {
    try {
      await logoutUser();
      localStorage.removeItem("token");
      localStorage.removeItem("user");
      setUser(null);
      console.log("User logged out");
      navigate("/login"); 
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };


  const register = async (userData) => {
    try {
      const response = await registerUser(userData);
      if (response.user) {
        localStorage.setItem("token", response.access_token);
        localStorage.setItem("user", JSON.stringify(response.user));
        setUser(response.user);
        console.log("User registered:", response.user);
        navigate("/"); 
        return { success: true };
      }
      return { success: false, error: response.error };
    } catch (error) {
      return { success: false, error: "Registration failed. Try again." };
    }
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, logout, register, setUser }}>
      {children}
    </AuthContext.Provider>
  );
};


export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) throw new Error("useAuth must be used within an AuthProvider");
  return context;
};

export default AuthContext;
