import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { GoogleLoginButton, GitHubLoginButton, LinkedInLoginButton } from "react-social-login-buttons";
import { registerUser, socialLogin } from "../services/authService";
import { updateUserProfile } from "../services/userService";

const Register = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    password: "",
    profilePhoto: null,
    location: "",
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    if (type === "file" && files[0]) {
      const reader = new FileReader();
      reader.readAsDataURL(files[0]);
      reader.onloadend = () => {
        setFormData((prevData) => ({ ...prevData, profilePhoto: reader.result }));
      };
    } else {
      setFormData((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      const user = await registerUser(formData);
      await updateUserProfile(user.id, {
        fullName: formData.fullName,
        email: formData.email,
        location: formData.location,
        profilePhoto: formData.profilePhoto,
      });
      navigate(`/profile/${user.id}`);
    } catch (err) {
      console.error("Registration error:", err);
      setError(err.message || "Registration failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleSocialLogin = async (provider) => {
    setError("");
    try {
      const user = await socialLogin(provider);
      navigate(`/profile/${user.id}`);
    } catch (err) {
      console.error("Social login error:", err);
      setError(err.message || "Social login failed. Please try again.");
    }
  };

  return (
    <div className="flex flex-col items-center p-6 bg-gray-100 min-h-screen">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md">
        <h2 className="text-2xl font-bold mb-4 text-center">Join Us</h2>
        {error && <p className="text-red-500 text-center">{error}</p>}

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="fullName"
            placeholder="Full Name"
            value={formData.fullName}
            onChange={handleChange}
            className="border p-2 w-full rounded"
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            className="border p-2 w-full rounded"
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            className="border p-2 w-full rounded"
            required
          />
          <input
            type="text"
            name="location"
            placeholder="Location (City, Country)"
            value={formData.location}
            onChange={handleChange}
            className="border p-2 w-full rounded"
          />
          <input
            type="file"
            name="profilePhoto"
            onChange={handleChange}
            className="border p-2 w-full rounded"
          />
          <button
            type="submit"
            className="bg-blue-600 text-white p-2 w-full rounded hover:bg-blue-700"
            disabled={loading}
          >
            {loading ? "Registering..." : "Sign Up"}
          </button>
        </form>

        <div className="mt-4">
          <p className="text-center text-gray-500">Or sign up with</p>
          <div className="flex flex-col space-y-2 mt-2">
            <GoogleLoginButton onClick={() => handleSocialLogin("google")} />
            <GitHubLoginButton onClick={() => handleSocialLogin("github")} />
            <LinkedInLoginButton onClick={() => handleSocialLogin("linkedin")} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
