import { useEffect, useState } from "react";
import { getTasks } from "../services/taskService"; 
import supabase from "../config/supabaseClient";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchTasks = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (err) {
      setError("Failed to fetch tasks. Please try again later.");
      console.error("Error fetching tasks:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();

    const channel = supabase
      .channel("tasks-realtime")
      .on(
        "postgres_changes",
        { event: "*", schema: "public", table: "tasks" },
        (payload) => {
          if (!payload.new && !payload.old) return;

          switch (payload.eventType) {
            case "INSERT":
              console.log("New task added:", payload.new);
              setTasks((prevTasks) => [...prevTasks, payload.new]);
              toast.success(`🆕 New Task: ${payload.new.title}`, { position: "top-right", autoClose: 3000 });
              break;

            case "UPDATE":
              console.log("Task updated:", payload.new);
              setTasks((prevTasks) =>
                prevTasks.map((task) => (task.id === payload.new.id ? payload.new : task))
              );
              toast.info(`✏️ Task Updated: ${payload.new.title}`);
              break;

            case "DELETE":
              console.log("Task deleted:", payload.old);
              setTasks((prevTasks) =>
                prevTasks.filter((task) => task.id !== payload.old.id)
              );
              toast.error(`🗑️ Task Removed`);
              break;

            default:
              break;
          }
        }
      )
      .subscribe();

    return () => {
      channel.unsubscribe();
    };
  }, []);

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold text-center mb-6">Available Tasks</h2>

      {loading && <p className="text-center text-gray-500">Loading tasks...</p>}

      {error && (
        <div className="text-center">
          <p className="text-red-500">{error}</p>
          <button 
            onClick={fetchTasks} 
            className="mt-3 px-4 py-2 bg-blue-600 text-white rounded-md shadow-md hover:bg-blue-700"
          >
            Retry
          </button>
        </div>
      )}

      {!loading && !error && tasks.length === 0 && (
        <p className="text-center text-gray-500">No tasks available.</p>
      )}

      {!loading && !error && tasks.length > 0 && (
        <ul className="space-y-4">
          {tasks.map((task) => (
            <li key={task.id} className="border rounded-lg p-4 shadow-md bg-white">
              <h3 className="text-lg font-semibold">{task.title}</h3>
              <p className="text-gray-700">{task.description}</p>
              <p className="text-blue-600 font-medium mt-2">Budget: ${task.budget}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskList;
