import React from 'react';
import { Link } from 'react-router-dom';
import { CheckCircle, Briefcase, Users, Star } from 'lucide-react';
import TaskList from './TaskList';

const Home = () => {
    return (
        <div>
            {/* Hero Section */}
            <div className="relative pt-16 bg-gradient-to-r from-blue-100 to-indigo-100 transform skew-y-6 origin-top-left">
                <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-24 md:py-32">
                    <div className="grid md:grid-cols-2 gap-12 items-center">
                        <div className="space-y-8">
                            <h1 className="text-5xl md:text-6xl font-extrabold">
                                <span className="block">Find Gigs,</span>
                                <span className="block text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-indigo-600">Earn Money</span>
                            </h1>
                            <p className="text-xl text-gray-600">
                                Discover and complete freelance tasks easily with QuickTask.
                            </p>
                            <div className="flex space-x-4">
                                <Link to="/tasks" className="bg-blue-600 text-white px-6 py-3 rounded-lg font-semibold shadow-lg">Browse Tasks</Link>
                                <Link to="/create-task" className="bg-yellow-400 text-black px-6 py-3 rounded-lg font-semibold shadow-lg">Post a Task</Link>
                            </div>
                        </div>
                        <div className="relative">
                            <img
                                src="https://images.unsplash.com/photo-1522202176988-66273c2fd55f"
                                alt="Freelancer at work"
                                className="rounded-2xl shadow-2xl transform hover:scale-105 transition-transform duration-500"
                            />
                        </div>
                    </div>
                </div>
            </div>

            {/* Featured Tasks Section */}
            <div className="py-24 bg-white">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <h2 className="text-3xl font-bold text-center">Featured Tasks</h2>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mt-8">
                        {[{
                            title: "Website Fix",
                            description: "Fix CSS issues on a portfolio site.",
                            price: "$50"
                        }, {
                            title: "Logo Design",
                            description: "Create a modern logo for a startup.",
                            price: "$75"
                        }, {
                            title: "Data Entry",
                            description: "Enter data from scanned receipts.",
                            price: "$30"
                        }].map((task, index) => (
                            <div key={index} className="bg-gray-100 p-6 shadow rounded-lg">
                                <h3 className="font-semibold text-lg">{task.title}</h3>
                                <p className="text-gray-600">{task.description}</p>
                                <span className="block mt-2 text-green-600 font-bold">{task.price}</span>
                            </div>
                        ))}
                    </div>
                    <div className="text-center mt-8">
                        <Link to="/tasks" className="text-blue-600 font-semibold">View More Tasks</Link>
                    </div>
                </div>
            </div>

            {/* Why QuickTask? */}
            <div className="py-24 bg-gray-50">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                    <h2 className="text-3xl font-bold">Why Use QuickTask?</h2>
                    <div className="grid md:grid-cols-3 gap-12 mt-8">
                        {[{
                            icon: <CheckCircle className="h-10 w-10 text-green-500" />, 
                            title: "Secure Payments", 
                            description: "Get paid securely for every completed task."
                        }, {
                            icon: <Briefcase className="h-10 w-10 text-blue-500" />, 
                            title: "Wide Range of Gigs", 
                            description: "Find tasks in various categories."
                        }, {
                            icon: <Users className="h-10 w-10 text-purple-500" />, 
                            title: "Trusted Community", 
                            description: "Work with verified clients and freelancers."
                        }].map((benefit, index) => (
                            <div key={index} className="text-center">
                                <div className="mb-4">{benefit.icon}</div>
                                <h3 className="text-xl font-bold mb-2">{benefit.title}</h3>
                                <p className="text-gray-600">{benefit.description}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Join a Group Section */}
            <div className="py-24 bg-white">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                    <h2 className="text-3xl font-bold">Join a Group</h2>
                    <p className="mt-4 text-xl text-gray-600">Connect with others, share ideas, and grow together in industry-specific groups.</p>
                    <div className="mt-8">
                        <Link to="/groups" className="bg-green-500 text-white px-6 py-3 rounded-lg font-semibold shadow-lg">Explore Groups</Link>
                    </div>
                </div>
            </div>

            {/* Industry-specific Groups */}
            <div className="py-24 bg-gray-50">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                    <h2 className="text-3xl font-bold">Industry-Specific Groups</h2>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-12 mt-8">
                        {[{
                            name: "Web Developers",
                            description: "Join a group of developers to discuss best practices and find collaborators."
                        }, {
                            name: "Graphic Designers",
                            description: "Network with fellow designers and share your work."
                        }, {
                            name: "Writers & Editors",
                            description: "Collaborate with writers and editors on various projects."
                        }].map((group, index) => (
                            <div key={index} className="bg-gray-100 p-6 shadow rounded-lg">
                                <h3 className="font-semibold text-lg">{group.name}</h3>
                                <p className="text-gray-600">{group.description}</p>
                                <Link to="/join-group" className="block mt-4 text-blue-600 font-semibold">Join Group</Link>
                            </div>
                        ))}
                    </div>
                    <div className="text-center mt-8">
                        <Link to="/groups" className="text-blue-600 font-semibold">View All Groups</Link>
                    </div>
                </div>
            </div>

            {/* Testimonials Section */}
            <div className="py-24 bg-gradient-to-r from-blue-100 to-indigo-100">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                    <h2 className="text-3xl font-bold">What Our Users Say</h2>
                    <div className="grid md:grid-cols-2 gap-12 mt-8">
                        {[{
                            name: "Alice M.",
                            feedback: "QuickTask helped me find gigs that paid instantly! Highly recommend."
                        }, {
                            name: "John D.",
                            feedback: "Posting a task was easy, and I got help within hours. Amazing platform!"
                        }].map((testimonial, index) => (
                            <div key={index} className="bg-white p-6 rounded-lg shadow-md">
                                <p className="italic">"{testimonial.feedback}"</p>
                                <p className="mt-2 font-bold">- {testimonial.name}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

