import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Sun, Moon, User, Menu } from 'lucide-react';

const Header = () => {
    const [darkMode, setDarkMode] = useState(false);
    const [menuOpen, setMenuOpen] = useState(false);
    const [userLoggedIn, setUserLoggedIn] = useState(true); // Simulating login state

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
        document.documentElement.classList.toggle('dark');
    };

    return (
        <header className={`p-4 bg-white shadow-md dark:bg-gray-900 transition duration-300`}> 
            <div className="max-w-7xl mx-auto flex justify-between items-center">
                {/* Logo */}
                <Link to="/" className="text-2xl font-bold text-blue-600 dark:text-blue-400">QuickTask</Link>

                {/* Navigation */}
                <nav className="hidden md:flex space-x-6">
                    <Link to="/tasks" className="hover:text-blue-600 dark:hover:text-blue-400 transition">Browse Tasks</Link>
                    <Link to="/create-task" className="hover:text-yellow-500 transition">Post a Task</Link>
                    <Link to="/ai-matching" className="hover:text-purple-500 transition">AI Matching</Link>
                    <Link to="/community" className="hover:text-green-500 transition">Community</Link>
                </nav>

                {/* Right Section */}
                <div className="flex items-center space-x-4">
                    {/* Search Bar */}
                    <input type="text" placeholder="Search tasks..." className="px-4 py-2 border rounded-lg hidden md:block focus:ring focus:ring-blue-300 dark:bg-gray-800 dark:text-white" />

                    {/* Dark Mode Toggle */}
                    <button onClick={toggleDarkMode} className="p-2 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700 transition">
                        {darkMode ? <Sun className="h-5 w-5 text-yellow-500" /> : <Moon className="h-5 w-5 text-gray-500" />}
                    </button>

                    {/* User Dropdown */}
                    {userLoggedIn ? (
                        <div className="relative">
                            <button onClick={() => setMenuOpen(!menuOpen)} className="flex items-center space-x-2 p-2 hover:bg-gray-200 dark:hover:bg-gray-700 rounded-lg">
                                <User className="h-6 w-6 text-gray-700 dark:text-gray-300" />
                                <span className="hidden md:inline">Account</span>
                            </button>
                            {menuOpen && (
                                <div className="absolute right-0 mt-2 bg-white dark:bg-gray-800 shadow-lg rounded-lg p-4 w-48">
                                    <Link to="/profile" className="block px-4 py-2 hover:bg-gray-200 dark:hover:bg-gray-700">Profile</Link>
                                    <Link to="/dashboard" className="block px-4 py-2 hover:bg-gray-200 dark:hover:bg-gray-700">Dashboard</Link>
                                    <button className="block w-full text-left px-4 py-2 hover:bg-gray-200 dark:hover:bg-gray-700">Logout</button>
                                </div>
                            )}
                        </div>
                    ) : (
                        <Link to="/login" className="bg-blue-600 text-white px-4 py-2 rounded-lg shadow-lg hover:bg-blue-700">Login</Link>
                    )}

                    {/* Mobile Menu */}
                    <button className="md:hidden p-2 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700" onClick={() => setMenuOpen(!menuOpen)}>
                        <Menu className="h-6 w-6" />
                    </button>
                </div>
            </div>
        </header>
    );
};

export default Header;
