import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import supabase from "../config/supabaseClient";

const UserProfile = () => {
  const { id } = useParams(); 
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    bio: "",
    skills: "",
    experience: "",
    education: "",
    linkedin: "",
    github: "",
    portfolio: "",
    profile_picture: "",
  });


  useEffect(() => {
    const fetchUser = async () => {
      const { data, error } = await supabase.from("users").select("*").eq("id", id).single();

      if (error) {
        console.error("Error fetching user:", error);
        alert("Failed to fetch user data.");
      } else {
        setUser(data);
        setFormData(data);
      }

      setLoading(false);
    };

    fetchUser();
  }, [id]);


  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

    const handleUpdateProfile = async () => {
    const { error } = await supabase.from("users").update(formData).eq("id", id);
    if (error) {
      console.error("Error updating profile:", error);
      alert("Failed to update profile.");
    } else {
      setUser(formData);
      setEditing(false);
    }
  };


  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    const filePath = `profiles/${id}/${file.name}`;


    const { error } = await supabase.storage.from("avatars").upload(filePath, file, { upsert: true });

    if (error) {
      console.error("Error uploading file:", error);
      alert("Failed to upload image.");
    } else {
      const { data } = supabase.storage.from("avatars").getPublicUrl(filePath);
      if (data) {
        setFormData({ ...formData, profile_picture: data.publicUrl });
      }
    }
    setUploading(false);
  };

  if (loading) return <p className="text-center text-gray-500">Loading user profile...</p>;
  if (!user) return <p className="text-center text-red-500">User not found.</p>;

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-md rounded-lg">
      <div className="flex items-center space-x-4">
        <img
          src={user.profile_picture || "https://via.placeholder.com/150"}
          alt="Profile"
          className="w-24 h-24 rounded-full object-cover border"
        />
        <div>
          <h2 className="text-2xl font-bold">{user.name}</h2>
          <p className="text-gray-600">{user.email}</p>
        </div>
      </div>

      <label className="block mt-3">
        <span className="text-gray-700">Profile Picture</span>
        <input type="file" onChange={handleFileUpload} className="block mt-1 w-full border p-2 rounded-md" />
        {uploading && <p className="text-sm text-gray-500">Uploading...</p>}
      </label>

      {editing ? (
        <div className="mt-4 space-y-3">
          <textarea
            name="bio"
            value={formData.bio}
            onChange={handleChange}
            className="w-full p-2 border rounded-md"
            placeholder="Add a bio..."
          />
          <input type="text" name="skills" value={formData.skills} onChange={handleChange} className="w-full p-2 border rounded-md" placeholder="Skills (comma-separated)" />
          <input type="text" name="experience" value={formData.experience} onChange={handleChange} className="w-full p-2 border rounded-md" placeholder="Experience" />
          <input type="text" name="education" value={formData.education} onChange={handleChange} className="w-full p-2 border rounded-md" placeholder="Education" />
          <input type="text" name="linkedin" value={formData.linkedin} onChange={handleChange} className="w-full p-2 border rounded-md" placeholder="LinkedIn Profile URL" />
          <input type="text" name="github" value={formData.github} onChange={handleChange} className="w-full p-2 border rounded-md" placeholder="GitHub Profile URL" />
          <input type="text" name="portfolio" value={formData.portfolio} onChange={handleChange} className="w-full p-2 border rounded-md" placeholder="Portfolio URL" />

          <button onClick={handleUpdateProfile} className="bg-blue-600 text-white px-4 py-2 rounded-md">Save Changes</button>
          <button onClick={() => setEditing(false)} className="ml-2 text-gray-600">Cancel</button>
        </div>
      ) : (
        <div className="mt-4">
          <p className="text-gray-700">{user.bio || "No bio available"}</p>

          <h3 className="mt-4 text-xl font-semibold">Skills</h3>
          <p>{user.skills || "No skills listed"}</p>

          <h3 className="mt-4 text-xl font-semibold">Experience</h3>
          <p>{user.experience || "No experience listed"}</p>

          <h3 className="mt-4 text-xl font-semibold">Education</h3>
          <p>{user.education || "No education listed"}</p>

          <h3 className="mt-4 text-xl font-semibold">Social Links</h3>
          <ul>
            {user.linkedin && (
              <li>
                <a href={user.linkedin} target="_blank" rel="noopener noreferrer" className="text-blue-600">LinkedIn</a>
              </li>
            )}
            {user.github && (
              <li>
                <a href={user.github} target="_blank" rel="noopener noreferrer" className="text-gray-700">GitHub</a>
              </li>
            )}
            {user.portfolio && (
              <li>
                <a href={user.portfolio} target="_blank" rel="noopener noreferrer" className="text-green-600">Portfolio</a>
              </li>
            )}
          </ul>

          <button onClick={() => setEditing(true)} className="mt-3 bg-gray-600 text-white px-4 py-2 rounded-md">
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
