import apiClient from "../utils/apiClient";
import supabase from "../utils/supabaseClient"; // Assuming you have a Supabase client utility

// Register a new user
export const registerUser = async (userData) => {
  try {
    const { fullName, email, password, profilePhoto, location } = userData;
    const { user, error: registerError } = await supabase.auth.signUp({
      email,
      password,
    });

    if (registerError) {
      throw registerError;
    }

    if (profilePhoto) {
      const fileExt = profilePhoto.name.split(".").pop();
      const filePath = `${user.id}/profile.${fileExt}`;
      const { data, error: uploadError } = await supabase.storage
        .from("avatars")
        .upload(filePath, profilePhoto);

      if (uploadError) {
        throw uploadError;
      }

      const profilePhotoUrl = data?.path
        ? `${supabase.storageUrl}/avatars/${data.path}`
        : null;

      const { error: updateError } = await supabase
        .from("users")
        .update({ fullName, location, profilePhoto: profilePhotoUrl })
        .eq("id", user.id);

      if (updateError) {
        throw updateError;
      }
    }

    await apiClient.put(`/users/${user.id}`, { profileCompleted: true });

    return { user, message: "Registration successful!" };
  } catch (error) {
    console.error("Registration Error:", error.message);
    throw error.message || { message: "Registration failed" };
  }
};

// Log in a user with provided credentials
export const loginUser = async (credentials) => {
  try {
    const { email, password } = credentials;
    const { user, session, error: loginError } = await supabase.auth.signIn({
      email,
      password,
    });

    if (loginError) {
      throw loginError;
    }

    localStorage.setItem("token", session.access_token);
    apiClient.defaults.headers.common["Authorization"] = `Bearer ${session.access_token}`;

    return { user, access_token: session.access_token };
  } catch (error) {
    console.error("Login Error:", error.message);
    throw error.message || { message: "Login failed" };
  }
};

// Social login (Google, Facebook, etc.)
export const socialLogin = async (provider) => {
  try {
    const { user, session, error: socialLoginError } = await supabase.auth.signIn({
      provider,
    });

    if (socialLoginError) {
      throw socialLoginError;
    }

    localStorage.setItem("token", session.access_token);
    apiClient.defaults.headers.common["Authorization"] = `Bearer ${session.access_token}`;

    return { user, access_token: session.access_token };
  } catch (error) {
    console.error("Social Login Error:", error.message);
    throw error.message || { message: "Social login failed" };
  }
};

// Log out the user
export const logoutUser = () => {
  supabase.auth.signOut();
  localStorage.removeItem("token");
  delete apiClient.defaults.headers.common["Authorization"];
};

// Verify user's email
export const verifyEmail = async (token) => {
  try {
    const response = await apiClient.post("/auth/verify-email", { token });
    return response.data;
  } catch (error) {
    console.error("Email Verification Error:", error.message);
    throw error.message || { message: "Email verification failed" };
  }
};
